     1import { useState } from 'react';
     2import { Box, useInput, Text } from 'ink';
     3import type { ToolCall, ToolApprovalDecision } from '@nuvin/nuvin-core';
     4import { ToolParameters } from './ToolParameters.js';
     5import { ToolProgressInfo } from './ToolProgressInfo.js';
     6import { ToolActions } from './ToolActions.js';
     7import { AppModal } from '@/components/AppModal.js';
     8import { useToolApproval } from '@/contexts/ToolApprovalContext.js';
     9import { theme } from '@/theme.js';
    10
    11type Props = {
    12  toolCalls: ToolCall[];
    13  onApproval: (decision: ToolApprovalDecision, approvedCalls?: ToolCall[]) => void;
    14  onCancel?: () => void;
    15};
    16
    17export function ToolApprovalPrompt({ toolCalls, onApproval }: Props) {
    18  const { addSessionApprovedTool } = useToolApproval();
    19  const [currentToolIndex, setCurrentToolIndex] = useState(0);
    20  const [approvedCalls, setApprovedCalls] = useState<ToolCall[]>([]);
    21  const [_deniedCalls, setDeniedCalls] = useState<ToolCall[]>([]);
    22  const [selectedAction, setSelectedAction] = useState(0); // 0=Yes, 1=No, 2=Yes for session
    23
    24  const currentTool = toolCalls[currentToolIndex];
    25  const isLastTool = currentToolIndex === toolCalls.length - 1;
    26
    27  // Handle keyboard navigation
    28  useInput((input, key) => {
    29    if (key.tab) {
    30      setSelectedAction((prev) => {
    31        if (key.shift) {
    32          return prev === 0 ? 2 : prev - 1;
    33        } else {
    34          return prev === 2 ? 0 : prev + 1;
    35        }
    36      });
    37      return;
    38    }
    39
    40    if (key.return) {
    41      const decisions = ['approve', 'deny', 'approve_session'] as const;
    42      handleToolDecision(decisions[selectedAction]);
    43      return;
    44    }
    45
    46    if (key.leftArrow) {
    47      setSelectedAction((prev) => (prev === 0 ? 2 : prev - 1));
    48      return;
    49    }
    50
    51    if (key.rightArrow) {
    52      setSelectedAction((prev) => (prev === 2 ? 0 : prev + 1));
    53      return;
    54    }
    55
    56    // Handle number key shortcuts
    57    if (input === '1') {
    58      handleToolDecision('approve');
    59      return;
    60    }
    61    if (input === '2') {
    62      handleToolDecision('deny');
    63      return;
    64    }
    65    if (input === '3') {
    66      handleToolDecision('approve_session');
    67      return;
    68    }
    69  });
    70
    71  const handleToolDecision = (decision: 'approve' | 'deny' | 'approve_session') => {
    72    if (decision === 'approve') {
    73      setApprovedCalls((prev) => [...prev, currentTool]);
    74    } else if (decision === 'deny') {
    75      setDeniedCalls((prev) => [...prev, currentTool]);
    76    } else if (decision === 'approve_session') {
    77      // Add current tool to session-approved list
    78      addSessionApprovedTool(currentTool.function.name);
    79
    80      // Find all tools with the same name in the current batch (current + remaining)
    81      const currentToolName = currentTool.function.name;
    82      const toolsWithSameName = toolCalls
    83        .slice(currentToolIndex)
    84        .filter((tool) => tool.function.name === currentToolName);
    85
    86      // Approve all instances of this tool in current batch
    87      setApprovedCalls((prev) => [...prev, ...toolsWithSameName]);
    88
    89      // Find the next tool with a different name
    90      const nextDifferentToolIndex = toolCalls
    91        .slice(currentToolIndex + 1)
    92        .findIndex((tool) => tool.function.name !== currentToolName);
    93
    94      if (nextDifferentToolIndex === -1) {
    95        // No more different tools - we're done, approve everything
    96        onApproval('approve', [...approvedCalls, ...toolsWithSameName]);
    97        return;
    98      }
    99
   100      // Move to the next different tool
   101      setCurrentToolIndex(currentToolIndex + 1 + nextDifferentToolIndex);
   102      setSelectedAction(0); // Reset to first action
   103      return;
   104    }
   105
   106    if (isLastTool) {
   107      // Last tool - send final decision
   108      const finalApproved = decision === 'approve' ? [...approvedCalls, currentTool] : approvedCalls;
   109      if (finalApproved.length > 0) {
   110        onApproval('approve', finalApproved);
   111      } else {
   112        onApproval('deny');
   113      }
   114    } else {
   115      // Move to next tool
   116      setCurrentToolIndex((prev) => prev + 1);
   117      setSelectedAction(0); // Reset to first action
   118    }
   119  };
   120
   121  return (
   122    <AppModal
   123      visible
   124      title={currentTool.function.name}
   125      rightTitle={
   126        <Text color={theme.toolApproval.description} dimColor>
   127          Tab/ Navigate  Enter Select  1/2/3 Quick Select
   128        </Text>
   129      }
   130    >
   131      <Box flexDirection="column" width="100%">
   132        {/* Parameters / Specialized Content */}
   133        <ToolParameters toolCall={currentTool} />
   134
   135        {/* Action Buttons at Bottom */}
   136        <Box flexDirection="row" justifyContent="space-between" alignItems="center" marginTop={1}>
   137          <ToolActions selectedAction={selectedAction} />
   138          <ToolProgressInfo currentIndex={currentToolIndex} totalTools={toolCalls.length} />
   139        </Box>
   140      </Box>
   141    </AppModal>
   142  );
   143}
