     1import { Box, Text } from 'ink';
     2import { useStdoutDimensions } from '@/hooks';
     3
     4export type DiffSegment = {
     5  text: string;
     6  type: 'unchanged' | 'add' | 'remove';
     7};
     8
     9export type DiffLine = {
    10  type: 'add' | 'remove' | 'context' | 'modify';
    11  content: string;
    12  oldLineNum?: number;
    13  newLineNum?: number;
    14  segments?: DiffSegment[];
    15};
    16
    17export type DiffBlock = {
    18  search: string;
    19  replace: string;
    20};
    21
    22export type LineNumbers = {
    23  oldStartLine: number;
    24  oldEndLine: number;
    25  newStartLine: number;
    26  newEndLine: number;
    27  oldLineCount: number;
    28  newLineCount: number;
    29};
    30
    31export function createSimpleDiff(search: string, replace: string, lineNumbers?: LineNumbers): DiffLine[] {
    32  // Remove trailing newline to avoid empty line at end
    33  const searchTrimmed = search.replace(/\n$/, '');
    34  const replaceTrimmed = replace.replace(/\n$/, '');
    35
    36  const searchLines = searchTrimmed.split('\n');
    37  const replaceLines = replaceTrimmed.split('\n');
    38  const diff: DiffLine[] = [];
    39
    40  // If content is identical, show as context
    41  if (searchTrimmed === replaceTrimmed) {
    42    searchLines.forEach((line, i) => {
    43      const realLineNum = lineNumbers ? lineNumbers.oldStartLine + i : i + 1;
    44      diff.push({ type: 'context', content: line, oldLineNum: realLineNum, newLineNum: realLineNum });
    45    });
    46    return diff;
    47  }
    48
    49  // Use LCS-based diff algorithm to find real changes
    50  const lcs = computeLCS(searchLines, replaceLines);
    51  const changes = buildDiffFromLCS(searchLines, replaceLines, lcs, lineNumbers);
    52
    53  return changes;
    54}
    55
    56// Compute Longest Common Subsequence
    57function computeLCS(a: string[], b: string[]): number[][] {
    58  const m = a.length;
    59  const n = b.length;
    60  const dp: number[][] = Array(m + 1)
    61    .fill(0)
    62    .map(() => Array(n + 1).fill(0));
    63
    64  for (let i = 1; i <= m; i++) {
    65    for (let j = 1; j <= n; j++) {
    66      if (a[i - 1] === b[j - 1]) {
    67        dp[i][j] = dp[i - 1][j - 1] + 1;
    68      } else {
    69        dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
    70      }
    71    }
    72  }
    73
    74  return dp;
    75}
    76
    77// Compute character-level diff for inline highlighting
    78function computeInlineDiff(oldText: string, newText: string): { old: DiffSegment[]; new: DiffSegment[] } {
    79  const oldWords = oldText.split(/(\s+)/);
    80  const newWords = newText.split(/(\s+)/);
    81
    82  const lcs = computeLCS(oldWords, newWords);
    83  const oldSegments: DiffSegment[] = [];
    84  const newSegments: DiffSegment[] = [];
    85
    86  let i = oldWords.length;
    87  let j = newWords.length;
    88
    89  const oldOps: { type: 'unchanged' | 'remove'; text: string }[] = [];
    90  const newOps: { type: 'unchanged' | 'add'; text: string }[] = [];
    91
    92  while (i > 0 || j > 0) {
    93    if (i > 0 && j > 0 && oldWords[i - 1] === newWords[j - 1]) {
    94      oldOps.unshift({ type: 'unchanged', text: oldWords[i - 1] });
    95      newOps.unshift({ type: 'unchanged', text: newWords[j - 1] });
    96      i--;
    97      j--;
    98    } else if (j > 0 && (i === 0 || lcs[i][j - 1] >= lcs[i - 1][j])) {
    99      newOps.unshift({ type: 'add', text: newWords[j - 1] });
   100      j--;
   101    } else if (i > 0) {
   102      oldOps.unshift({ type: 'remove', text: oldWords[i - 1] });
   103      i--;
   104    }
   105  }
   106
   107  // Merge consecutive segments of same type
   108  for (const op of oldOps) {
   109    if (oldSegments.length > 0 && oldSegments[oldSegments.length - 1].type === op.type) {
   110      oldSegments[oldSegments.length - 1].text += op.text;
   111    } else {
   112      oldSegments.push({ type: op.type, text: op.text });
   113    }
   114  }
   115
   116  for (const op of newOps) {
   117    if (newSegments.length > 0 && newSegments[newSegments.length - 1].type === op.type) {
   118      newSegments[newSegments.length - 1].text += op.text;
   119    } else {
   120      newSegments.push({ type: op.type, text: op.text });
   121    }
   122  }
   123
   124  return { old: oldSegments, new: newSegments };
   125}
   126
   127// Calculate similarity ratio between two strings
   128function similarityRatio(str1: string, str2: string): number {
   129  const longer = str1.length > str2.length ? str1 : str2;
   130  if (longer.length === 0) return 1.0;
   131
   132  const editDistance = levenshteinDistance(str1, str2);
   133  return (longer.length - editDistance) / longer.length;
   134}
   135
   136function levenshteinDistance(str1: string, str2: string): number {
   137  const m = str1.length;
   138  const n = str2.length;
   139  const dp: number[][] = Array(m + 1)
   140    .fill(0)
   141    .map(() => Array(n + 1).fill(0));
   142
   143  for (let i = 0; i <= m; i++) dp[i][0] = i;
   144  for (let j = 0; j <= n; j++) dp[0][j] = j;
   145
   146  for (let i = 1; i <= m; i++) {
   147    for (let j = 1; j <= n; j++) {
   148      if (str1[i - 1] === str2[j - 1]) {
   149        dp[i][j] = dp[i - 1][j - 1];
   150      } else {
   151        dp[i][j] = Math.min(dp[i - 1][j], dp[i][j - 1], dp[i - 1][j - 1]) + 1;
   152      }
   153    }
   154  }
   155
   156  return dp[m][n];
   157}
   158
   159// Build diff from LCS table with inline diff support
   160function buildDiffFromLCS(
   161  oldLines: string[],
   162  newLines: string[],
   163  lcs: number[][],
   164  lineNumbers?: LineNumbers,
   165): DiffLine[] {
   166  let i = oldLines.length;
   167  let j = newLines.length;
   168  let oldLineNum = lineNumbers ? lineNumbers.oldEndLine : i;
   169  let newLineNum = lineNumbers ? lineNumbers.newEndLine : j;
   170
   171  const operations: DiffLine[] = [];
   172
   173  while (i > 0 || j > 0) {
   174    if (i > 0 && j > 0 && oldLines[i - 1] === newLines[j - 1]) {
   175      operations.unshift({
   176        type: 'context',
   177        content: oldLines[i - 1],
   178        oldLineNum,
   179        newLineNum,
   180      });
   181      i--;
   182      j--;
   183      oldLineNum--;
   184      newLineNum--;
   185    } else if (j > 0 && (i === 0 || lcs[i][j - 1] >= lcs[i - 1][j])) {
   186      operations.unshift({
   187        type: 'add',
   188        content: newLines[j - 1],
   189        newLineNum,
   190      });
   191      j--;
   192      newLineNum--;
   193    } else if (i > 0) {
   194      operations.unshift({
   195        type: 'remove',
   196        content: oldLines[i - 1],
   197        oldLineNum,
   198      });
   199      i--;
   200      oldLineNum--;
   201    }
   202  }
   203
   204  // Post-process: merge consecutive remove+add into modify with inline diff
   205  const finalOps: DiffLine[] = [];
   206  for (let idx = 0; idx < operations.length; idx++) {
   207    const curr = operations[idx];
   208    const next = operations[idx + 1];
   209
   210    if (
   211      curr.type === 'remove' &&
   212      next?.type === 'add' &&
   213      curr.oldLineNum &&
   214      next.newLineNum &&
   215      similarityRatio(curr.content, next.content) > 0.5
   216    ) {
   217      // Merge into a modify line with inline diff
   218      const inlineDiff = computeInlineDiff(curr.content, next.content);
   219
   220      // Create two separate lines: one for old (remove), one for new (add)
   221      finalOps.push({
   222        type: 'modify',
   223        content: curr.content,
   224        oldLineNum: curr.oldLineNum,
   225        segments: inlineDiff.old,
   226      });
   227      finalOps.push({
   228        type: 'modify',
   229        content: next.content,
   230        newLineNum: next.newLineNum,
   231        segments: inlineDiff.new,
   232      });
   233
   234      idx++; // Skip next since we've processed it
   235    } else {
   236      finalOps.push(curr);
   237    }
   238  }
   239
   240  return finalOps;
   241}
   242
   243type DiffLineViewProps = {
   244  line: DiffLine;
   245  maxWidth?: number;
   246  lineNumWidth?: number;
   247};
   248
   249export function DiffLineView({ line, maxWidth = 80, lineNumWidth = 3 }: DiffLineViewProps) {
   250  const lineNum = line.oldLineNum || line.newLineNum || 0;
   251  const lineNumStr = `${String(lineNum).padStart(lineNumWidth, ' ')}`;
   252
   253  // Handle modify type with inline segments
   254  if (line.type === 'modify' && line.segments) {
   255    const prefix = line.oldLineNum ? '- ' : '+ ';
   256    const isRemoveLine = !!line.oldLineNum;
   257    const lineBaseBg = isRemoveLine ? 'red' : 'green';
   258
   259    // Calculate total length and truncate if needed
   260    let totalLength = 0;
   261    const segments: typeof line.segments = [];
   262    // const overhead = lineNumStr.length + 3; // line number + prefix
   263
   264    for (const segment of line.segments) {
   265      const remaining = maxWidth - totalLength;
   266      if (remaining <= 0) break;
   267
   268      if (totalLength + segment.text.length > maxWidth) {
   269        segments.push({ ...segment, text: `${segment.text.slice(0, remaining)}` });
   270        totalLength = maxWidth;
   271        break;
   272      } else {
   273        segments.push(segment);
   274        totalLength += segment.text.length;
   275      }
   276    }
   277
   278    return (
   279      <Box>
   280        <Text dimColor color="gray">
   281          {lineNumStr}
   282        </Text>
   283        <Text color="gray"> </Text>
   284        <Text backgroundColor={lineBaseBg} color="white">
   285          {prefix}
   286        </Text>
   287        {segments.map((segment, segIdx) => {
   288          let segmentBg: string | undefined = lineBaseBg;
   289          const segmentFg = 'white';
   290
   291          if (segment.type === 'add') {
   292            segmentBg = 'greenBright';
   293          } else if (segment.type === 'remove') {
   294            segmentBg = 'redBright';
   295          }
   296
   297          return (
   298            <Text key={`segment-${segIdx}-${segment.type}`} backgroundColor={segmentBg} color={segmentFg}>
   299              {segment.text}
   300            </Text>
   301          );
   302        })}
   303      </Box>
   304    );
   305  }
   306
   307  // Handle regular add/remove/context lines
   308  const prefix = line.type === 'add' ? '+ ' : line.type === 'remove' ? '- ' : '  ';
   309  const content = line.content.length > maxWidth ? `${line.content.slice(0, maxWidth)}` : line.content;
   310
   311  const bgColor = line.type === 'add' ? 'green' : line.type === 'remove' ? 'red' : undefined;
   312  const fgColor = line.type === 'add' || line.type === 'remove' ? 'white' : 'gray';
   313
   314  return (
   315    <Box>
   316      <Text dimColor color="gray">
   317        {lineNumStr}
   318      </Text>
   319      <Text color="gray"> </Text>
   320      <Text backgroundColor={bgColor} color={fgColor}>
   321        {prefix}
   322        {content}
   323      </Text>
   324    </Box>
   325  );
   326}
   327
   328type FileDiffViewProps = {
   329  blocks: DiffBlock[];
   330  filePath?: string;
   331  showPath?: boolean;
   332  lineNumbers?: LineNumbers;
   333};
   334
   335export function FileDiffView({ blocks, filePath, showPath = true, lineNumbers }: FileDiffViewProps) {
   336  const {cols} = useStdoutDimensions()
   337  return (
   338    <Box flexDirection="column">
   339      {showPath && filePath && (
   340        <Box marginLeft={2}>
   341          <Text color="cyan">path: </Text>
   342          <Text>{filePath}</Text>
   343        </Box>
   344      )}
   345      {blocks.map((b, idx) => {
   346        const diff = createSimpleDiff(b.search, b.replace, lineNumbers);
   347        const hasChanges = diff.some((d) => d.type !== 'context');
   348
   349        // Calculate max line number width for proper alignment
   350        const maxLineNum = Math.max(...diff.map((line) => Math.max(line.oldLineNum || 0, line.newLineNum || 0)));
   351        const lineNumWidth = String(maxLineNum).length;
   352
   353        return (
   354          <Box key={`block-${b.replace}`} flexDirection="column">
   355            {blocks.length > 1 && (
   356              <Text color="magenta" dimColor>
   357                 Block {idx + 1}/{blocks.length} 
   358              </Text>
   359            )}
   360            {hasChanges ? (
   361              <Box flexDirection="column">
   362                {diff.map((line, ldx) => (
   363                  <DiffLineView
   364                    maxWidth={cols - 22}
   365                    key={`line-${idx}-${ldx}-${line.content}`}
   366                    line={line}
   367                    lineNumWidth={lineNumWidth}
   368                  />
   369                ))}
   370              </Box>
   371            ) : (
   372              <Text color="gray" dimColor>
   373                {' '}
   374                (no changes)
   375              </Text>
   376            )}
   377          </Box>
   378        );
   379      })}
   380      {blocks.length === 0 && (
   381        <Box marginLeft={2}>
   382          <Text color="red">No changes to display</Text>
   383        </Box>
   384      )}
   385    </Box>
   386  );
   387}
