     6
     7type ToolParametersProps = {
     8  toolCall: ToolCall;
     9};
    10
    11export const ToolParameters: React.FC<ToolParametersProps> = ({ toolCall }) => {
    12  const { theme } = useTheme();
    13
    14  const formatToolArguments = (call: ToolCall) => {
    15    try {
    16      const args = call.function.arguments ? JSON.parse(call.function.arguments) : {};
    17      const toolName = call.function.name;
