    83      case 'file_read':
    84        return { text: isSuccess ? 'Read' : 'Read failed', color: statusColor, paramText };
    85      case 'file_new':
    86        return { text: isSuccess ? 'Created' : 'Creation failed', color: statusColor, paramText };
