    98        return { text: isSuccess ? 'Updated ' : 'Update failed', color: statusColor, paramText };
    99      case 'dir_ls':
   100        return { text: isSuccess ? `Listed` : `Listing failed`, color: statusColor, paramText };
   101      default:
   102        return { text: toolResult.status, color: statusColor, paramText };
   103    }
   104  };
   105  const renderContent = () => {
   106    switch (toolResult.name) {
   107      case 'assign_task': {
   108        let resultStr =
