   131          <DefaultRenderer
   132            toolResult={toolResult}
   133            messageId={messageId}
   134            messageContent={messageContent}
   135            messageColor={messageColor}
   136          />
   137        );
   138    }
   139  };
   140
   141  const { text, color } = getStatusMessage();
   142  const content = renderContent();
   143
   144  const shouldShowResult =
   145    (toolResult.result !== null && toolResult.result !== undefined && toolResult.result !== '') ||
   146    toolResult.name === 'todo_write';
   147
   148  return (
   149    <Box marginLeft={2} flexDirection="column">
