1import type { ToolCall } from '@nuvin/nuvin-core';
     2import { readFile } from 'node:fs/promises';
     3import { resolve } from 'node:path';
     4
     5export type LineNumbers = {
     6  oldStartLine: number;
     7  oldEndLine: number;
     8  newStartLine: number;
     9  newEndLine: number;
    10  oldLineCount: number;
    11  newLineCount: number;
    12};
    13
    14export type EnrichedToolCall = ToolCall & {
    15  metadata?: {
    16    lineNumbers?: LineNumbers;
    17    [key: string]: unknown;
    18  };
    19};
    20
    21function normalizeToLF(text: string): string {
    22  return text.replace(/\r\n/g, '\n');
    23}
    24
    25function calculateLineNumbers(originalText: string, oldText: string, matchIndex: number, newText: string): LineNumbers {
    26  const textBeforeMatch = originalText.slice(0, matchIndex);
    27  const oldStartLine = textBeforeMatch.split('\n').length;
    28  
    29  const oldTextTrimmed = oldText.replace(/\n$/, '');
    30  const oldLineCount = oldTextTrimmed.length === 0 ? 0 : oldTextTrimmed.split('\n').length;
    31  const oldEndLine = oldStartLine + oldLineCount - 1;
    32  
    33  const newTextTrimmed = newText.replace(/\n$/, '');
    34  const newLineCount = newTextTrimmed.length === 0 ? 0 : newTextTrimmed.split('\n').length;
    35  const newStartLine = oldStartLine;
    36  const newEndLine = newStartLine + newLineCount - 1;
    37
    38  return {
    39    oldStartLine,
    40    oldEndLine,
    41    newStartLine,
    42    newEndLine,
    43    oldLineCount,
    44    newLineCount,
    45  };
    46}
    47
    48async function enrichFileEditToolCall(toolCall: ToolCall): Promise<EnrichedToolCall> {
    49  try {
    50    const args = toolCall.function.arguments ? JSON.parse(toolCall.function.arguments) : {};
    51    if (!args.file_path || args.old_text === undefined || args.new_text === undefined) {
    52      return toolCall;
    53    }
    54
    55    const filePath = resolve(args.file_path);
    56    const content = await readFile(filePath, 'utf-8');
    57    const contentLF = normalizeToLF(content);
    58    const oldTextLF = normalizeToLF(args.old_text);
    59    const newTextLF = normalizeToLF(args.new_text);
    60
    61    const matchIndex = contentLF.indexOf(oldTextLF);
    62    if (matchIndex === -1) {
    63      return toolCall;
    64    }
    65
    66    const lineNumbers = calculateLineNumbers(contentLF, oldTextLF, matchIndex, newTextLF);
    67
    68    return {
    69      ...toolCall,
    70      metadata: {
    71        lineNumbers,
    72      },
    73    };
    74  } catch {
    75    return toolCall;
    76  }
    77}
    78
    79export async function enrichToolCallsWithLineNumbers(toolCalls: ToolCall[]): Promise<EnrichedToolCall[]> {
    80  return Promise.all(
    81    toolCalls.map((call) => {
    82      if (call.function.name === 'file_edit') {
    83        return enrichFileEditToolCall(call);
    84      }
    85      return call;
    86    }),
    87  );
    88}
