   243type DiffLineViewProps = {
   244  line: DiffLine;
   245  maxWidth?: number;
   246  lineNumWidth?: number;
   247};
   248
   249export function DiffLineView({ line, maxWidth = 80, lineNumWidth = 3 }: DiffLineViewProps) {
   250  const lineNum = line.oldLineNum || line.newLineNum || 0;
   251  const lineNumStr = `${String(lineNum).padStart(lineNumWidth, ' ')}`;
   252
   253  // Handle modify type with inline segments
   254  if (line.type === 'modify' && line.segments) {
   255    const prefix = line.oldLineNum ? '- ' : '+ ';
   256    const isRemoveLine = !!line.oldLineNum;
   257    const lineBaseBg = isRemoveLine ? 'red' : 'green';
   258
   259    // Calculate total length and truncate if needed
   260    let totalLength = 0;
   261    const segments: typeof line.segments = [];
   262    // const overhead = lineNumStr.length + 3; // line number + prefix
   263
   264    for (const segment of line.segments) {
   265      const remaining = maxWidth - totalLength;
   266      if (remaining <= 0) break;
   267
   268      if (totalLength + segment.text.length > maxWidth) {
   269        segments.push({ ...segment, text: `${segment.text.slice(0, remaining)}` });
   270        totalLength = maxWidth;
   271        break;
   272      } else {
   273        segments.push(segment);
   274        totalLength += segment.text.length;
   275      }
   276    }
   277
   278    return (
   279      <Box>
   280        <Text dimColor color="gray">
   281          {lineNumStr}
   282        </Text>
   283        <Text color="gray"> </Text>
   284        <Text backgroundColor={lineBaseBg} color="white">
   285          {prefix}
   286        </Text>
   287        {segments.map((segment, segIdx) => {
   288          let segmentBg: string | undefined = lineBaseBg;
   289          const segmentFg = 'white';
   290
   291          if (segment.type === 'add') {
   292            segmentBg = 'greenBright';
   293          } else if (segment.type === 'remove') {
   294            segmentBg = 'redBright';
   295          }
   296
   297          return (
   298            <Text key={`segment-${segIdx}-${segment.type}`} backgroundColor={segmentBg} color={segmentFg}>
   299              {segment.text}
   300            </Text>
   301          );
   302        })}
   303      </Box>
   304    );
   305  }
   306
